var fs = require('fs');
exports.name = 'rules';
exports.usage = '';
exports.desc = 'list rules';

function listRules() {
  console.log(global.cwd + '/rules/')
  fs.readdir(global.cwd + '/rules/', function(error, files) {
    if (files.length > 0) {
      console.log('Exist Custom Rules:');
      console.log(files.join('\r\n'));
    } else {
      console.log('Not find Exist Rules!');
    }
  });
  fs.readdir(__dirname + '/rules/', function(error, files) {
    if (files.length > 0) {
      console.log('Exist Sys Rules:');
      console.log(files.join('\r\n'));
    } else {
      console.log('Not find Exist Rules!');
    }
  });
}


exports.register = function(commander) {
    listRules();
};
